import React, { useState } from 'react';
import {
  Box,
  Grid,
  Button,
  TextField,
  Card,
  CardContent,
  Typography,
  Divider,
  Chip,
} from '@mui/material';
import './SettingsPage.css'; //  src/pages directory

const SettingsPage = () => {
  const [ip, setIp] = useState('');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleUpdateConfig = () => {
    const updatedConfig = {
      ssh: {
        host: ip,
        username: username,
        password: password,
      },
    };
    console.log('Updated Config:', updatedConfig);
    // Add logic to save the updated configuration
  };

  return (
    <Grid
      container
      spacing={0}
      direction="column"
      alignItems="center"
      justify="center"
      style={{ minHeight: '100vh' }}
    >
      <Card sx={{ minWidth: 800, mb: 4 }} style={{ backgroundColor: '#026ca0' }}>
        <CardContent>
          <Typography gutterBottom component="div" align="center" sx={{ color: 'white', fontSize: 60, mt: 4, textTransform: 'uppercase', height: 80 }}>
            Settings
          </Typography>
          <Divider><Chip label="." /></Divider>
          <Box component="form" sx={{ mt: 3 }}>
            <TextField
              fullWidth
              label="IP Address"
              variant="outlined"
              value={ip}
              onChange={(e) => setIp(e.target.value)}
              sx={{ mb: 2 }}
            />
            <TextField
              fullWidth
              label="Username"
              variant="outlined"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
              sx={{ mb: 2 }}
            />
            <TextField
              fullWidth
              label="Password"
              variant="outlined"
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              sx={{ mb: 2 }}
            />
            <Button
              variant="contained"
              color="primary"
              onClick={handleUpdateConfig}
              sx={{ mt: 2 }}
            >
              Update Configuration
            </Button>
          </Box>
        </CardContent>
      </Card>
    </Grid>
  );
};

export default SettingsPage;
